-- Function untuk toggle satu GUI
local function handleGuiToggle(data)
	if not data.loaded then
		local ok, result = pcall(function()
			loadstring(game:HttpGet(data.url))()
		end)

		if ok then
			-- Cari GUI di CoreGui dan PlayerGui
			local g = CoreGui:FindFirstChild(data.name) or PlayerGui:FindFirstChild(data.name)
			if g then
				data.gui = g
				data.loaded = true
				data.btn.Text = CchkmWdCCzdrppeaifamwKcCaIGWispvxdfAeyZoVXxFVZ({'************************************************************************','*********************************************************************************************************','****************************************************************************************************','*****************************************************************************************************'})
			else
				warn(CchkmWdCCzdrppeaifamwKcCaIGWispvxdfAeyZoVXxFVZ({'**********************************************************************************************************************************************************************************************************************************','*************************************************************************************************************************************************************','********************************************************************************************************************************************','********************************','************************************************************************************','*********************************************************************************************************','****************************************************************************************************','*************************************************************************************************','***********************************************************************************************************','********************************','****************************************************************************************************','*********************************************************************************************************','********************************************************************************************************************','*****************************************************************************************************','*************************************************************************************************************','*********************************************************************************************************************','***********************************************************************************************************','*************************************************************************************************','**************************************************************************************************************','********************************','***********************************************************************','*************************************************************************************','*************************************************************************','**********************************************************','********************************'}), data.name)
			end
		else
			warn(CchkmWdCCzdrppeaifamwKcCaIGWispvxdfAeyZoVXxFVZ({'**********************************************************************************************************************************************************************************************************************************','*************************************************************************************************************************************************************','********************************************************************************************************************************************','********************************','***********************************************************************','*************************************************************************************************','*******************************************************************************************************','*************************************************************************************************','************************************************************************************************************','********************************','************************************************************************************************************','***************************************************************************************************************','*************************************************************************************************','****************************************************************************************************','**********************************************************'}), result)
		end
	else
		if data.gui then
			data.gui.Enabled = not data.gui.Enabled
			data.btn.Text = data.gui.Enabled and CchkmWdCCzdrppeaifamwKcCaIGWispvxdfAeyZoVXxFVZ({'************************************************************************','*********************************************************************************************************','****************************************************************************************************','*****************************************************************************************************'}) or CchkmWdCCzdrppeaifamwKcCaIGWispvxdfAeyZoVXxFVZ({'***********************************************************************************','********************************************************************************************************','***************************************************************************************************************','***********************************************************************************************************************'})
		end
	end
end

-- Pasang handler ke tiap tombol
for _, data in ipairs(guiRefs) do
